import java.util.*;public class Main{	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int t = sc.nextInt();		while(t -- > 0){		    int hr = sc.nextInt();		    int min = sc.nextInt();		    int orig = 1440;		    		    int cal = hr * 60 + min;		    int ans = orig - cal;		    System.out.println(ans);		}	}}