import java.util.*;public class Main{	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int t = sc.nextInt();		while(t -- > 0){		    int n = sc.nextInt();		    HashMap<Integer,Integer> map = new HashMap<>();		    int arr[] = new int[n];		    for(int i = 0; i < n ;i++){		        arr[i] = sc.nextInt();		        		        if(map.containsKey(arr[i])){		            map.put(arr[i],map.getOrDefault(arr[i],0) + 1);		        }		        else{		            map.put(arr[i], 1);		        }		    }		    int ans = -1;		    for(Map.Entry<Integer,Integer> entry : map.entrySet()){		        if(entry.getValue() >= 3){		            ans = entry.getKey();		        }		    }		    System.out.println(ans);		}	}}