import java.util.*;public class cf3{    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while(t -- > 0){            int a = sc.nextInt();            int b = sc.nextInt();            int c = sc.nextInt();            int d = sc.nextInt();            int val = 0;            val = a;            int count = 0;            if(val < b){                count++;            }            if(val < c){                count++;            }            if(val < d){                count++;            }            System.out.println(count);        }    }}