import java.util.*;public class Main{	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int t = sc.nextInt();		while(t -- > 0){		    int n = sc.nextInt();		    String s = sc.next();		    int freq[] = new int[26];		    int ans = 0;		    		    for(int i = 0; i < s.length(); i++){		        char ch = s.charAt(i);		        if(ch >= 'A' && ch <= 'Z'){		            freq[ch - 'A']++;		            if(freq[ch - 'A'] == 1){		                ans += 2;		            }		            else{		                ans += 1;		            }		        }		    }		    System.out.println(ans);		}	}}