import java.util.*;public class Main{	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int t = sc.nextInt();		while(t -- > 0){		String s = sc.next();		String s1 = "codeforces";		int i = 0;		int j = 0;		int count = 0;		while(i < s.length()){		    if(s.charAt(i) == s1.charAt(j)){		        i++;		        j++;		    }		    else{		        count++;		        i++;		        j++;		    }		}		System.out.println(count);		}	}}