import java.util.*;public class Main{	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int t = sc.nextInt();		while(t -- > 0){		    int a = sc.nextInt();		    int b = sc.nextInt();		    int c = sc.nextInt();		    boolean flag = false;		    		    if(a + b >= 10 && flag == false){		        flag = true;		    }		    if(a + c >= 10 && flag == false){		        flag = true;		    }		    if(b + c >= 10 && flag == false){		        flag = true;		    }		    		    if(flag){		        System.out.println("YES");		    }		    else{		        System.out.println("NO");		    }		}	}}