import java.util.*;public class Main{	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int t = sc.nextInt();		while(t -- > 0){		    String s = sc.next();		    String actual = "abc";		    int i = 0, j = 0;		    int count = 0;		    while(i < s.length()){		        if(s.charAt(i) != actual.charAt(j)){		            count++;		            i++;		            j++;		        }		        else{		            i++;		            j++;		        }		    }		    if(count == 3){		        System.out.println("No");		    }		    else{		        System.out.println("Yes");		    }		}	}}