import java.util.*;public class Main{	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int t = sc.nextInt();		while(t -- > 0){		    int n = sc.nextInt();		    String s = sc.next();		    char ch[] = s.toCharArray();		    int fi = -1;		    int li = -1;		    		    for(int i = 0; i < ch.length; i++){		        if(ch[i] == 'B'){		            fi = i;		            break;		        }		    }		    for(int i = ch.length - 1; i >= 0; i--){		        if(ch[i] == 'B'){		            li = i;		            break;		        }		    }		    int ans = 0;		    ans = li - fi + 1;		    System.out.println(ans);		}	}}