import java.util.*;public class Main{	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int t = sc.nextInt();		while(t -- > 0){		String a = sc.next();		String b = sc.next();		char fi = a.charAt(0);		char se = b.charAt(0);		if(fi != se){		    StringBuilder sb = new StringBuilder();		    StringBuilder sb1 = new StringBuilder();		    sb.append(se);		    sb.append(a.charAt(1));		    sb.append(a.charAt(2));		    		    sb1.append(fi);		    sb1.append(b.charAt(1));		    sb1.append(b.charAt(2));		    		    System.out.println(sb.toString()+ " " + sb1.toString());		}		else{		    System.out.println(a + " " + b);		}		}		}}