import java.util.*;public class Main{	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int n = sc.nextInt();		if(n >= 0){		    System.out.print(n);		}		else{		    int last = n / 10;		    int rem = n % 10;		    int sec = (n / 100 ) * 10 + rem;		    System.out.print(Math.max(last,sec));		}	}}