import java.util.*;public class cf3{    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        String str = sc.next();        char ch[] = str.toCharArray();        StringBuilder sb = new StringBuilder();        int i = 0;        while(i < ch.length){            if(ch[i] == '.'){                sb.append(0);                i++;            }            else if(ch[i] == '-'){                if(ch[i + 1] == '.'){                    sb.append(1);                }                else{                    sb.append(2);                }                i = i + 2;            }        }        System.out.println(sb.toString());        }}