import java.util.*;public class Main{	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		char dir = sc.nextLine().charAt(0);		String enter = sc.next();		String key = "qwertyuiopasdfghjkl;zxcvbnm,./";		int shift = 0;		if(dir == 'R'){		    shift = -1;		}		else{		    shift = 1;		}		StringBuilder sb = new StringBuilder();		for(int i = 0; i < enter.length(); i++){		    char ch = enter.charAt(i);		    int indx = 0;		    indx = key.indexOf(ch);		    sb.append(key.charAt(indx + shift));		}		System.out.print(sb.toString());	}}