import java.util.*;public class Main{	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		String s = sc.next();		StringBuilder sb = new StringBuilder();				for(int i = 0; i < s.length(); i++){		    int digit = s.charAt(i) - '0';		    if(i == 0 && digit == 9){		        sb.append(digit);		    }		    else{		        sb.append(Math.min(digit,9 - digit));		    }		}		System.out.print(sb.toString());			}}